import { useState } from "react"
const Blog = ({blog}) => {
  const [showMore, setShowMore] = useState(false)
  
   if (!showMore) {
    return (
      <div>
        <div className='basicView' data-testid='divBasicView' onClick={() => setShowMore(true)}>
          {blog.title} - {blog.author}
        </div>
      </div>
    )
  }
  

}

export default Blog
