import { useState } from "react"
const Blog = ({blog}) => {
  const [showMore, setShowMore] = useState(true)

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5,
    display: 'block'
  }
   if (!showMore) {
    return (
      <div>
        <div className='basicView' style={blogStyle} data-testid='divBasicView'>
          <button onClick={() => setShowMore(true)}>view</button>
          {blog.title} - {blog.author}
        </div>
      </div>
    )
  }
  return (
    <div>
      <div className='expandedView' style={blogStyle} data-testid='divExpandedView'>
      <button onClick={() => setShowMore(false)}>hide</button>
        <p>
          {blog.title} - {blog.author}
        </p>
        <a href={blog.url}> {blog.url} </a>
        <p>Likes: {blog.likes}</p>

    
      </div>
    </div>
  );
  

}

export default Blog
